import streamlit as st

# Title of the apps
st.markdown("<style>.stApp {background-image: url('data:image/jpeg;base64,'); background-size: cover;}</style>", unsafe_allow_html=True)
st.title("ü©∫ Medical Diagnosis System")

st.write("Enter your symptom below to get a basic health recommendation:")

# Input from user
symptom = st.text_input("Symptom", placeholder="e.g. fever, cough, headache")

# Simple recommendation logic
if st.button("Get Recommendation"):
    if symptom == "":
        st.warning("‚ö†Ô∏è Please enter a symptom.")
    else:
        symptom = symptom.lower()
        if "fever" in symptom:
            st.success("You may have a mild infection or flu. Stay hydrated and rest. If it continues, consult a doctor.")
        elif "cough" in symptom:
            st.success("You might have a throat infection or cold. Drink warm fluids and seek medical help if it lasts long.")
        elif "headache" in symptom:
            st.success("It could be due to stress, dehydration, or migraine. Drink water and take rest.")
        elif "stomach" in symptom:
            st.success("Possible indigestion or infection. Avoid oily food and drink plenty of water.")
        else:
            st.info("Symptom not found in database. Please consult a healthcare professional for accurate diagnosis.")
